/* ==UserStyle==
@name           Google Search Result Center Align
@namespace      USO Archive
@author         paulcjy
@description    `Well working style.`
@version        20220203.17.16
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("google.com") {
.tsf {
    margin: 0 auto;
}
#pTwnEc {
    display: flex;
}
#hdtb-msb {
    margin: 0 auto;
    padding-right: 166px;
}
#rcnt {
    display: flex;
    max-width: 100%;
    width: 100%;
}
.D6j0vc {
    display: flex;
    max-width: 100%;
    width: 100%;
}
#center_col {
    margin: 0 auto;
}
#rhs {
    position: absolute;
    padding-left: 400px;
    margin: 0 0 0 50%;
}
#slim_appbar {
    display: flex;
    margin: 0;
}
.LHJvCe {
    margin: 0 auto;
    position: relative;
}
#hdtbMenus {
    width: 998px;
    margin-left: calc(50% - 506px);
}
.c3mZkd {
    margin-left: calc(50% - 326px);
}
}

/* ==UserStyle==
@name           Github Catppuccin
@namespace      github.com/catppuccin/github
@version        1.1.14
@description    Soothing pastel theme for GitHub
@author         Catppuccin
@updateURL      https://github.com/catppuccin/github/raw/main/catppuccin.user.css
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var select accent-color "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Gray"]
@var checkbox topbar "Topbar colors" 1
@var checkbox usefont "Use Inter font" 0
==/UserStyle== */

@-moz-document regexp("https:\/\/*github\.com") {

  if (theme=="Latte") {
    $type = light
    $rosewater = #dc8a78
    $flamingo = #dd7878
    $pink = #ea76cb
    $mauve = #8839ef
    $red = #d20f39
    $maroon = #e64553
    $peach = #fe640b
    $yellow = #df8e1d
    $green = #40a02b
    $teal = #179299
    $sky = #04a5e5
    $sapphire = #209fb5
    $blue = #1e66f5
    $lavender = #7287fd
    $text = #4c4f69
    $subtext1 = #5c5f77
    $subtext0 = #6c6f85
    $overlay2 = #7c7f93
    $overlay1 = #8c8fa1
    $overlay0 = #9ca0b0
    $surface2 = #acb0be
    $surface1 = #bcc0cc
    $surface0 = #ccd0da
    $base = #eff1f5
    $mantle = #e6e9ef
    $crust = #dce0e8
    $shadow = $crust
  }

  else if (theme=="Frappe") {
    $type = dark
    $rosewater = #f2d5cf
    $flamingo = #eebebe
    $pink = #f4b8e4
    $mauve = #ca9ee6
    $red = #e78284
    $maroon = #ea999c
    $peach = #ef9f76
    $yellow = #e5c890
    $green = #a6d189
    $teal = #81c8be
    $sky = #99d1db
    $sapphire = #85c1dc
    $blue = #8caaee
    $lavender = #babbf1
    $text = #c6d0f5
    $subtext1 = #b5bfe2
    $subtext0 = #a5adce
    $overlay2 = #949cbb
    $overlay1 = #838ba7
    $overlay0 = #737994
    $surface2 = #626880
    $surface1 = #51576d
    $surface0 = #414559
    $base = #303446
    $mantle = #292c3c
    $crust = #232634
    $shadow = $crust
  }

  else if (theme=="Macchiato") {
    $type = dark
    $rosewater = #f4dbd6
    $flamingo = #f0c6c6
    $pink = #f5bde6
    $mauve = #c6a0f6
    $red = #ed8796
    $maroon = #ee99a0
    $peach = #f5a97f
    $yellow = #eed49f
    $green = #a6da95
    $teal = #8bd5ca
    $sky = #91d7e3
    $sapphire = #7dc4e4
    $blue = #8aadf4
    $lavender = #b7bdf8
    $text = #cad3f5
    $subtext1 = #b8c0e0
    $subtext0 = #a5adcb
    $overlay2 = #939ab7
    $overlay1 = #8087a2
    $overlay0 = #6e738d
    $surface2 = #5b6078
    $surface1 = #494d64
    $surface0 = #363a4f
    $base = #24273a
    $mantle = #1e2030
    $crust = #181926
    $shadow = $crust
  }

  else if (theme=="Mocha") {
    $type = dark
    $rosewater = #f5e0dc
    $flamingo = #f2cdcd
    $pink = #f5c2e7
    $mauve = #cba6f7
    $red = #f38ba8
    $maroon = #eba0ac
    $peach = #fab387
    $yellow = #f9e2af
    $green = #a6e3a1
    $teal = #94e2d5
    $sky = #89dceb
    $sapphire = #74c7ec
    $lavender = #b4befe
    $blue = #89b4fa
    $text = #cdd6f4
    $subtext1 = #bac2de
    $subtext0 = #a6adc8
    $overlay2 = #9399b2
    $overlay1 = #7f849c
    $overlay0 = #6c7086
    $surface2 = #585b70
    $surface1 = #45475a
    $surface0 = #313244
    $base = #1e1e2e
    $mantle = #181825
    $crust = #11111b
    $shadow = $crust
  }

  if (accent-color=="Rosewater") {
    $accent-color = $rosewater;
  } else if (accent-color=="Flamingo") {
    $accent-color = $flamingo
  } else if (accent-color=="Pink") {
    $accent-color = $pink
  } else if (accent-color=="Mauve") {
    $accent-color = $mauve
  } else if (accent-color=="Red") {
    $accent-color = $red
  } else if (accent-color=="Maroon") {
    $accent-color = $maroon
  } else if (accent-color=="Peach") {
    $accent-color = $peach
  } else if (accent-color=="Yellow") {
    $accent-color = $yellow
  } else if (accent-color=="Green") {
    $accent-color = $green
  } else if (accent-color=="Teal") {
    $accent-color = $teal
  } else if (accent-color=="Blue") {
    $accent-color = $blue
  } else if (accent-color=="Sapphire") {
    $accent-color = $sapphire
  } else if (accent-color=="Sky") {
    $accent-color = $sky
  } else if (accent-color=="Lavender") {
    $accent-color = $lavender
  }  else if (accent-color=="Gray") {
    $accent-color = $subtext0
  }
  /* Functions */
  hoverColor(col) {
    return blend(alpha(col, 0.7), $text)
  }

  activeColor(col) {
    return blend(alpha(col, 0.5), $text)
  }

  disabledColor(col) {
    return blend(alpha(col, 0.4), $base)
  }

}



@-moz-document regexp("https:\/\/(gist\.)*github\.com(?!\/(marketplace|organizations\/plan)).*") {

  [data-color-mode="light"][data-light-theme*="dark"],
  [data-color-mode="light"][data-light-theme*="light"],
  [data-color-mode="dark"][data-dark-theme*="dark"],
  [data-color-mode="dark"][data-dark-theme*="light"],
  [data-color-mode="auto"][data-dark-theme*="dark"],
  [data-color-mode="auto"][data-dark-theme*="light"] {

    &, & body {
      color-scheme: $type;

      --color-canvas-default-transparent: rgba(13, 17, 23, 0);
