        --color-gray-1100: $text;
        --color-gray-1000: $text;
        --color-gray-900: $text;
        --color-gray-800: $overlay2;
        --color-gray-700: $overlay1;
        --color-gray-600: $overlay0;
        --color-gray-500: $surface2;
        --color-gray-400: $surface1;
        --color-gray-300: $surface0;
        --color-gray-200: $mantle;
        --color-gray-100: $crust;
    }
    .site-theme-dark .nav-unscoped {
        background-color: rgb($mantle) !important;
    }
    /*----Tracking Status List Stuff----*/
    .site-theme-dark  .list[data-v-5776f768], .el-icon-arrow-down:before {
        color: rgb($mantle) !important;
    }
    .site-theme-dark .el-dropdown-menu, .site-theme-dark .el-dropdown-menu__item--divided:before, .el-select-dropdown {
        background-color: rgb($base) !important;
    }
    .site-theme-dark .el-dropdown-menu {
        border: 4px rgb($text) !important;
    }
    .site-theme-dark .el-dropdown-menu__item:focus, .el-dropdown-menu__item:not(.is-disabled):hover {
        color: rgb($crust) !important;
    }
    .site-theme-dark .el-dropdown-menu__item--divided {
        border-top-color: rgb(21,31,46);
    }
    .el-select-dropdown__item.hover, .el-select-dropdown__item:hover {
        background-color: rgb($mantle);
    }
    /*-----------------GREEN FIXES-----------------*/
    .user-page-unscoped.green {
         --color-blue: $green;
        --color-blue-dim: $green;
        --color-overlay: $crust;
    }
    .user-page-unscoped.green .progress .bar {
        background: linear-gradient(270deg,rgb($green), rgb($green));
    }

    /*-----------------BLUE FIXES-----------------*/
    .user-page-unscoped.blue {
        --color-blue: $blue;
       --color-blue-dim: $blue;
       --color-overlay: $crust;
    }
   .user-page-unscoped.purple .progress .bar {
         background: linear-gradient(270deg,rgb($blue), rgb($blue));
    }

    /*-----------------PURPLE FIXES-----------------*/
    .user-page-unscoped.purple {
         --color-blue: $mauve !important;
        --color-blue-dim: $mauve;
        --color-overlay: $crust;
    }
    .user-page-unscoped.purple .progress .bar {
          background: linear-gradient(270deg,rgb($mauve), rgb($mauve));
    }

    /*-----------------ORANGE FIXES-----------------*/
    .user-page-unscoped.orange {
        --color-blue: $peach !important;
       --color-blue-dim: $peach;
       --color-overlay: $crust;
    }
   .user-page-unscoped.orange .progress .bar {
         background: linear-gradient(270deg,rgb($peach), rgb($peach));
    }

    /*-----------------RED FIXES-----------------*/
    .user-page-unscoped.red {
        --color-blue: $red !important;
       --color-blue-dim: $red;
       --color-overlay: $crust;
    }
   .user-page-unscoped.red .progress .bar {
         background: linear-gradient(270deg,rgb($red), rgb($red));
    }

    /*-----------------PINK FIXES-----------------*/
    .user-page-unscoped.pink {
    --color-blue: $pink !important;
   --color-blue-dim: $pink;
   --color-overlay: $crust;
    }
    .user-page-unscoped.pink .progress .bar {
     background: linear-gradient(270deg,rgb($pink), rgb($pink));
    }
    /*-----------------GRAY FIXES-----------------*/
    .user-page-unscoped.gray {
    --color-blue: $overlay1 !important;
   --color-blue-dim: $overlay1;
   --color-overlay: $crust;
    }
    .user-page-unscoped.gray .progress .bar {
     background: linear-gradient(270deg,rgb($overlay1), rgb($overlay1));
    }

    /*---------SCORE TWEAKS--------------*/
    .score[data-v-5ca094da] { color: rgb($base); }

    /*--------------FOOTER (It's hardcoded idk why anilist dumdum)----------*/
    .footer[data-v-a5dce466], .landing[data-v-3428d21c], .footer, .footer[data-v-0f519cab]  {
        background-color: var(--color-background) !important;
    }

    /*------------------MISC EDITS--------------------*/
    /*--Reset button in notifications-----*/
    .reset-btn {
        color: rgb($base) !important;
    }
    /* Publish Button in Replies and Messages */
    .save {
        color: rgb($base) !important;
    }
    /*------------IMAGE COVER------------*/
    .cover .image-text, .plus-progress {
        color: rgb($text) !important;
    }

    .entry .plus-progress, .medialist.table .entry .edit { color: rgb($base) !important;}
    /*--------Settings button--------*/
    .settings[data-v-3deab077] .button {
        color: rgb($crust);
    }
    .el-checkbox__input.is-checked .el-checkbox__inner, .el-radio__input.is-checked .el-radio__inner {
        background-color: rgb($accent);
        border-color: rgb($accent);
    }
    .el-checkbox__inner:after {
        border-color: rgb($crust);
        border-width: 2px;
        left: 3px;
    }
    .el-radio__input.is-checked+.el-radio__label {
        color: rgb($accent);
    }
    /*--------HIDE SCROLLBAR IN BIO-----*/
    .section::-webkit-scrollbar {
        display: none;
    }
    .section {
        -ms-overflow-style: none;
        scrollbar-width: none;
    }
    /*--------ANICHART SPECIFIC TWEAKS----*/
    .nav[data-v-e2f25004] {
        background: rgb($crust);
    }
    .season[data-v-e2f25004], .charts .label[data-v-e2f25004], .charts .svg-icon[data-v-e2f25004], .section:first-of-type .section-heading[data-v-309146c2], a.title[data-v-09928904] {
        color: rgb($text);
    }
    .season.router-link-active[data-v-e2f25004], .charts a:hover .svg-icon[data-v-e2f25004], .season[data-v-e2f25004]:hover, .charts .router-link-active .svg-icon[data-v-e2f25004] {
        color: rgb($accent);
    }
    .settings[data-v-3deab077] .button.danger { color: rgb($base); }
    /* AUTOMAIL TWEAK */
    .site-theme-dark .el-popper .el-dropdown-menu__item:focus, .el-popper .el-dropdown-menu__item:not(.is-disabled):hover {
        color: rgb($accent) !important;
    }
    .hohMediaScore { font-size: 90%; font-family: Overpass; border: 2px solid rgb($surface0); padding: 4px; border-radius: 10px; margin-left: 0px; background-color: rgb($base); color: rgb($accent); }
        .filter-group.compare-btn, .hohButton { color: rgb($base); }
    .hohButton { background-color: rgb($accent); }
    .medialist.table .entry:hover, .medialist.table .entry .title a:hover, .compare-btn[data-v-170179fc]:hover { color: rgb($base); }
    .hohCompare table { background-color: rgb($crust);  }
        .hohAnimeTable, .hohAnimeTable td, .hohUserRow td, .hohUserRow th, .hohHeaderRow td, .hohHeaderRow th { border: 2px solid rgb($surface0) !important; }
        .hohUserRow td { border-top: 4px solid rgb($surface0) !important; }
    .hohUserRow tr { border-right: 4px solid rgb($surface0) !important; }
    /*---------ANILIST LIKER PLUGIN------*/
    #AnilistLikerPlugin .mmd2 {
        font-style: normal !important;
        font-weight: 700;
        font-family: Overpass;
    }
    #AnilistLikerPlugin  .btntop { margin-bottom: -4px; margin-top: 5px; }
    #AnilistLikerPlugin .btnbottom { margin-top: -2px; }
}

/* ==UserStyle==
@name           Notion Catppuccin
@namespace      https://github.com/OlaoluwaM
@version        1.0.2
@description    Soothing pastel theme for Notion
@author         OlaoluwaM
@homepageURL    https://github.com/OlaoluwaM/notion-catppuccin
@updateURL      https://userstyles.world/api/style/3838.user.css

==/UserStyle== */
@-moz-document url-prefix("https://www.notion.so")
{
    :root {
        --rosewater: 10, 56%, 91%;
        --flamingo: 0, 59%, 88%;
        --mauve: 279, 70%, 83%;
        --pink_theme: 316, 72%, 86%;
        --maroon: 349, 60%, 77%;
        --red_theme: 342, 79%, 75%;
        --peach: 024, 88%, 78%;
        --yellow_theme: 041, 88%, 84%;
        --green_theme: 128, 58%, 79%;
        --green_darken: 128, 58%, 76%;
        --green_darkest: 128, 58%, 72%;
        --teal: 171, 53%, 81%;
        --blue_theme: 207, 93%, 79%;
        --blue_darken: 213, 84%, 78%;
        --sky: 189, 71%, 73%;
        --lavender: 238, 100%, 89%;

        --black0: 254, 25%, 10%;
        --black1: 249, 23%, 12%;
        --black2: 240, 21%, 15%;
        --black3: 249, 18%, 22%;
        --black4: 254, 12%, 36%;
        --gray0: 247, 8%, 46%;
        --gray1: 274, 11%, 59%;
        --gray2: 285, 10%, 75%;
        --white: 220, 38%, 89%;

        --bg: hsl(var(--black1));
        --bg-light: hsl(var(--black1));
        --bg-lighter: hsla(var(--black1));
        --fg: hsla(var(--white), 1);
        --fg-light: hsl(var(--white));
        --fg-lighter: hsl(var(--fg-light));
        --main: hsl(var(--blue_darken), 0.8);
        --hover: hsl(var(--black3), 0.8);
        --border: hsl(var(--black3));
        --selection: hsla(var(--gray0), 0.5);
        --gray: hsl(var(--gray2));
        --brown: hsl(var(--flamingo));
        --orange: hsl(var(--peach));
        --yellow: hsl(var(--yellow_theme));
        --green: hsl(var(--green_theme));
        --blue: hsl(var(--blue_theme));
        --purple: hsl(var(--mauve));
        --pink: hsl(var(--pink_theme));
        --red: hsl(var(--red_theme));
        --bg-gray: hsl(var(--black3));
        --bg-brown: hsla(var(--flamingo), 0.9);
        --bg-orange: hsl(var(--peach), 0.9);
        --bg-yellow: hsla(var(--yellow_theme), 0.9);
        --bg-green: hsla(var(--green_darken), 0.9);
        --bg-blue: hsla(var(--blue_darken), 0.9);
        --bg-purple: hsla(var(--mauve), 0.9);
        --bg-pink: hsla(var(--pink_theme), 0.9);
        --bg-red: hsl(var(--red_theme), 0.9);

        --text-gray: hsla(var(--white), 0.5);
    }

    /* For Notion Enhancer*/

    :root.dark.dark {
        --theme--bg_secondary: hsl(var(--black2));
        --theme_text: hsl(var(--white));
        --theme--ui_interactive-hover: hsl(var(--black3));
        --theme--ui_interactive-active: hsl(var(--black4));
        --indentation_lines--color: var(--text-gray);
        --theme--ui_corner_action: hsla(var(--black4), 0.5);
        --theme--ui_corner_action-hover: hsla(var(--black4), 0.9);
    }

    /* Inline Code block */
    div.notion-selectable.notion-callout-block > div > div {
        background: hsla(var(--black3), 0.5) !important;
    }

    /* prettier-ignore */
    div.notion-dark-theme :where(
      *[style*='background: rgb(137, 99, 42)'],
      *[style*='background: rgb(73, 47, 100)'],
      *[style*='background: rgb(43, 89, 63)'],
      *[style*='background: rgb(133, 76, 29)'],
      *[style*='background: rgb(133, 76, 29)'],
      *[style*='background: rgb(40, 69, 108)'],
      *[style*='background: rgb(105, 49, 76)'],
      *[style*='background: rgb(110, 54, 48)'],
      *[style*='background: rgb(96, 59, 44)']
   ) {
/* prettier-ignore */
  color: hsl(var(--black1)) !important;
}

    div.notion-dark-theme div[style*="background: rgb(46, 170, 220)"] {
        background: hsla(var(--blue_darken), 1) !important;
        color: hsl(var(--black1)) !important;
        border-right: 0.5px solid hsl(var(--black3), 0.5) !important;
    }

    div.notion-dark-theme div[style*="background: rgb(46, 170, 220)"] svg {
        background: hsla(var(--blue_darken), 1) !important;
        fill: hsl(var(--black1)) !important;
    }

    div.notion-dark-theme *[style*="background: rgb(32, 32, 32)"] {
        background: hsl(var(--black2)) !important;
    }

    div.notion-dark-theme *[style*="background: rgb(37, 37, 37)"] {
        background: hsl(var(--black3)) !important;
    }

    div.notion-dark-theme
        *[style*="color: rgba(255, 255, 255, 0.87)"]:not([style*="color: rgba(255, 255, 255, 0.565)"]) {
        color: hsl(var(--white)) !important;
        -webkit-text-fill-color: currentColor;
    }

    div.notion-dark-theme *[style*="color:rgba(155, 155, 155, 1)"] {
        color: var(--text-gray) !important;
        fill: currentColor !important;
        -webkit-text-fill-color: currentColor !important;
    }

    div.notion-dark-theme
        *[style*="color:rgba(155, 155, 155, 1)"]
        .notion-focusable:hover {
        color: var(--white) !important;
        -webkit-text-fill-color: currentColor !important;
    }

    div.notion-dark-theme
        div[style*="color: rgba(255, 255, 255, 0.87)"]
        > div:after {
        color: hsla(var(--white), 0.3) !important;
        -webkit-text-fill-color: currentColor !important;
    }

    div.notion-dark-theme div.notion-help-button {
        background: hsla(var(--black4), 0.5) !important;
    }

    div.notion-dark-theme div.notion-help-button:hover {
        background: hsla(var(--black4), 0.9);
    }

    .notion-dark-theme
        *[style*="background: rgb(25, 25, 25)"]
        > div[style*="color: rgba(255, 255, 255, 0.565)"] {
        color: var(--text-gray) !important;
        -webkit-text-fill-color: currentColor !important;
    }

    /* For the Heading placeholder when a heading is created with no text */
    div.notion-dark-theme
        div[style*="-webkit-text-fill-color: rgb(55, 55, 55)"]:after {
        color: hsla(var(--gray0), 0.4) !important;
        -webkit-text-fill-color: currentColor !important;
    }

    /* For the command prompt. The 'Type / for command' thing*/
    div.notion-dark-theme
        div[style*="-webkit-text-fill-color: rgba(255, 255, 255, 0.443)"]:after {
        color: hsla(var(--gray1), 0.6) !important;
        -webkit-text-fill-color: currentColor !important;
    }

    /* For Settings dropdown */
    div.notion-dark-theme div[style*="background-color: rgb(37, 37, 37)"] {
        background-color: hsl(var(--black3)) !important;
    }

    /* Edit Template Overlay */
    div.notion-dark-theme div[style*="background: rgb(55, 60, 63)"] {
        background: var(--bg-gray) !important;
    }

    /* Quote Block */
    .notion-dark-theme .notion-quote-block {
        font-style: italic;
    }

    /* For the Grays */
    /* rgb(55, 55, 55) is Light Gray*/
    div.notion-dark-theme div[style*="background: rgb(55, 55, 55)"] {
        background-color: hsl(var(--gray0)) !important;
    }

    /* rgb(90, 90, 90) is Regular Gray*/
    div.notion-dark-theme div[style*="background: rgb(90, 90, 90)"] {
        background-color: hsl(var(--black4)) !important;
    }

    /* For layout blocks */
    .notion-dark-theme a[style*="background: rgba(255, 255, 255, 0.055)"] {
        background-color: hsl(var(--black3)) !important;
    }

    /* For nested quotes */
    /* .notion-dark-theme .notion-quote-block .notion-quote-block div div[placeholder] {
    color: var(--text-gray) !important
} */

    /*
For links
.notion-dark-theme a.notion-link-token {
    background: hsl(var(--rosewater)) !important;
    color: hsl(var(--black0)) !important;
    padding: 4px !important;
    padding-top: 6px !important;
    border-radius: 5px;
    caret-color: currentColor !important
}
*/

    /* From https://github.com/notionblog/themes/ */

    /*
background: rgb(63, 68, 71) => rgb(32, 32, 32)
background: rgb(47, 52, 55) => rgb(25, 25, 25)
rgb(37, 37, 37) => rgb(55, 60, 63)
background: rgb(47, 47, 47) => rgb(88, 91, 93)
rgba(255, 255, 255, 0.055) => rgba(255, 255, 255, 0.1)
border:rgb(32,32,32) => rgb(37, 37, 37)
boder-right: rgb(32, 32, 32) => rgb(63, 66, 69)
border-bottom: rgb(60, 63, 67) =>  rgb(37, 37, 37)
rgb(150, 150, 150) => rgb(159, 164, 169)
 => rgba(255, 255, 255, 0.03)
 rgba(255, 255, 255, 0.73) => rgba(202, 204, 206, 0.6)
rgba(255, 255, 255, 0.73) => rgba(255, 255, 255, 0.9)
rgba(255, 255, 255, 0.39) =>  rgba(25, 23, 17, 0.6)
*/

    /* Elements: [SideBar, popup]*/
    .notion-dark-theme *[style*="background: rgb(32, 32, 32)"],
    .notion-dark-theme *[style*="background-color: rgb(32, 32, 32)"] {
        background: var(--bg-lighter) !important;
    }

    /* Elements: [Background, CollectionViewSwitcherHeader ] */
    .notion-dark-theme *[style*="background: rgb(25, 25, 25)"],
    .notion-dark-theme *[style*="background-color: rgb(25, 25, 25)"],
    .notion-dark-theme
        div[style*="background: linear-gradient(rgba(25, 25, 25, 0) 0px, rgb(25, 25, 25) 10px, rgb(25, 25, 25) 100%)"] {
        background: var(--bg) !important;
    }

    /* Collection blocks light background */
    .notion-dark-theme *[style*="background: rgb(37, 37, 37)"],
    .notion-dark-theme *[style*="background: rgba(255, 255, 255, 0.6)"] {
        background: var(--bg-lighter) !important;
    }

    /* Firefox collection white background fix */

    .notion-dark-theme
        div[style*="rgba(0, 0, 0, 0) linear-gradient(rgba(47, 52, 55, 0) 0px, rgb(47, 52, 55) 10px, rgb(47, 52, 55) 100%) repeat scroll 0% 0%"] {
        background: none !important;
    }

    /* Collection block at click background */

    .notion-dark-theme *[style*="background: rgb(47, 47, 47)"],
    .notion-dark-theme *[style*="background: rgb(61, 66, 69)"] {
        background: var(--bg-light) !important;
    }

    /* Elements: [SVG fill gray] */

    .notion-dark-theme *[style*="fill: rgba(255, 255, 255, 0.73)"] {
        fill: var(--fg-lighter) !important;
    }

    /* Elements: [SVG fill blue] */
    .notion-dark-theme *[style*="fill:#2EAADC"],
    .notion-dark-theme *[style*="fill: rgb(46, 170, 220)"] {
        fill: var(--main) !important;
    }

    /* Elements: [Text, Table of content] */
    .notion-dark-theme *[style*="color: rgba(255, 255, 255, 0.73)"] {
        color: var(--fg) !important;
    }

    .notion-dark-theme *[style*="color: rgb(150, 150, 150)"] {
        color: var(--fg-light) !important;
    }

    /* ligther text */
    .notion-dark-theme *[style*="color: rgba(255, 255, 255, 0.39)"] {
        color: var(--fg-lighter) !important;
    }

    /* Untitled text*/
    .notion-dark-theme *[style*="-webkit-text-fill-color: rgb(53, 53, 53)"] {
        -webkit-text-fill-color: var(--fg-lighter) !important;
    }

    /* blue color */
    .notion-dark-theme *[style*="color: rgb(46, 170, 220)"],
    .notion-dark-theme *[style*="color:#2EAADC"] {
        color: var(--main) !important;
    }

    /* Elements: [Main Button,Main button at hover]  */
    .notion-dark-theme div[style*="background: rgb(0, 141, 190)"],
    .notion-dark-theme div[style*="background: rgb(46, 170, 220)"],
    .notion-dark-theme div[style*="background: rgb(6, 156, 205)"] {
        background: var(--main) !important;
    }
    .notion-dark-theme div[style*="background: rgb(6, 156, 205)"]:hover {
        /* background: var(--bg-lighter) !important;*/
        opacity: 0.8 !important;
    }

    /* hover background */

    .notion-dark-theme
        .notion-focusable
        div[style*="background: rgba(255, 255, 255, 0.055)"] {
        background: var(--hover) !important;
    }

    /* collection view border*/
    .notion-dark-theme *[style*="border: 1px solid rgb(37, 37, 37)"] {
        border: 1px solid var(--border) !important;
    }
    .notion-dark-theme *[style*="border-right: 1px solid rgb(37, 37, 37)"],
    .notion-dark-theme *[style*="border-right: 1px solid rgb(32, 32, 32)"] {
        border-right: 1px solid var(--border) !important;
    }
    .notion-dark-theme *[style*="border-bottom: 1px solid rgb(37, 37, 37)"] {
        border-bottom: 1px solid var(--border) !important;
    }
    .notion-dark-theme *[style*="border-top: 1px solid rgb(37, 37, 37)"] {
        border-top: 1px solid var(--border) !important;
    }

    /* Collection View box shadow */
    .notion-dark-theme
        *[style*="box-shadow: rgb(25, 25, 25) -3px 0px 0px, rgb(37, 37, 37) 0px 1px 0px"] {
        box-shadow: var(--border) -3px 0px 0px, var(--border) 0px 1px 0px !important;
    }
    /* simple table box shadow*/
    .notion-dark-theme
        *[style*="background: linear-gradient(270deg, rgb(25, 25, 25) 0%, rgba(25, 25, 25, 0) 100%)"] {
        background: linear-gradient(
            270deg,
            rgb(25 27 28 / 45%) 0%,
            rgb(195 154 154 / 0%) 100%
        ) !important;
    }

    /* scrollbar */
    .notion-dark-theme .notion-scroller::-webkit-scrollbar {
        width: 0.4rem;
        height: 0.4rem;
        background: var(--bg);
    }
    .notion-dark-theme .notion-scroller::-webkit-scrollbar * {
        background: transparent;
    }
    .notion-dark-theme .notion-scroller::-webkit-scrollbar-thumb {
        background: var(--bg-lighter) !important;
        cursor: pointer;
        border-radius: 1.6rem;
    }
    .notion-dark-theme .notion-scroller::-webkit-scrollbar-track {
        background: var(--bg) !important;
    }

    /* Selection */
    .notion-dark-theme *::selection,
    .notion-selectable-halo {
        background: var(--selection) !important;
    }

    /* Text background, blocks background, tags background */
    /*
gray rgba(47, 47, 47, 1) => rgba(60, 65, 68, 1)
brown rgba(54, 40, 34, 1) => rgba(76, 61, 53, 1)
orange rgba(66, 47, 34, 1) => rgba(85, 59, 41, 1)
yellow rgba(64, 51, 36, 1) => rgba(79, 64, 41, 1)
green rgba(35, 49, 42, 1) => rgba(46, 68, 58, 1)
blue rgba(27, 45, 56, 1) => rgba(45, 66, 86, 1)
purple rgba(48, 39, 57, 1) => rgba(69, 58, 91, 1)
pink rgba(59, 39, 48, 1) => rgba(81, 56, 77, 1)
red rgba(62, 40, 37, 1) => rgba(94, 52, 54, 1)
*/
    .notion-dark-theme *[style*="background:rgba(47, 47, 47, 1)"],
    .notion-dark-theme *[style*="background: rgb(47, 47, 47)"],
    .notion-dark-theme *[style*="background: rgb(77, 77, 77)"] {
        background: var(--bg-gray) !important;
    }
    .notion-dark-theme *[style*="background:rgba(54, 40, 34, 1)"],
    .notion-dark-theme *[style*="background: rgb(47, 39, 35)"],
    .notion-dark-theme *[style*="background: rgb(96, 59, 44)"] {
        background: var(--bg-brown) !important;
    }
    .notion-dark-theme *[style*="background:rgba(66, 47, 34, 1)"],
    .notion-dark-theme *[style*="background: rgb(56, 40, 30)"],
    .notion-dark-theme *[style*="background: rgb(133, 76, 29)"] {
        background: var(--bg-orange) !important;
