/* ==UserStyle==
@name           twitter.com - 5/21/2023, 5:15:08 PM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document url-prefix("https://twitter.com") {
    /* Twitter Catppuccin Mocha Blue */
    /* Page Main Background */
    :root {
        --base: #24273a;
        --mantle: #1e2030;
        --accent: #b7bdf8;
    }

    main,
    header {
        background-color: var(--base);
    }
    /* I don't want twitter blue, thank you very much */
    [aria-label="Twitter Blue"] {
        display: none;
    }
    /* Tweets Style */
    .r-kemksi {
        background-color: var(--mantle);
    }
    .r-5zmot {
        background-color: rgba(36, 39, 58, 0.5);
    }
    /* Buttons */
    .r-l5o3uw,
    .r-1vtznih {
        background-color: var(--accent);
    }
    /* Links or/and clickable */
    .r-1cvl2hr {
        color: var(--accent);
    }
    /* idk the boxes on the left */
    .r-g2wdr4 {
        background-color: var(--mantle);
    }
    /* Search Box */
    .r-42olwf {
        border-color: var(--mantle);
    }
    /* Epic border colors */
    .r-vhj8yc {
        border-color: var(--accent)
    }
    }

/* ==UserStyle==
@name           Youtube Music Catppuccin
@namespace      github.com/catppuccin/youtubemusic
@version        0.3.0
@description    Soothing pastel theme for Youtube Music
@author         Catppuccin
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var select accent-color "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Text"]
==/UserStyle== */

@-moz-document domain("music.youtube.com") {

    if (theme == "Latte") {
        @import url("https://catppuccin.github.io/youtubemusic/src/latte.css");
    } else if (theme == "Frappe") {
        @import url("https://catppuccin.github.io/youtubemusic/src/frappe.css");
    } else if (theme == "Macchiato") {
        @import url("https://catppuccin.github.io/youtubemusic/src/macchiato.css");
    } else if (theme == "Mocha") {
        @import url("https://catppuccin.github.io/youtubemusic/src/mocha.css");
    }

    html:not(.style-scope) {    
        if (accent-color=="Rosewater") {
          --ctp-accent: var(--ctp-rosewater) !important;
      } else if (accent-color=="Flamingo") {
          --ctp-accent: var(--ctp-flamingo) !important;
      } else if (accent-color=="Pink") {
          --ctp-accent: var(--ctp-pink) !important;
      } else if (accent-color=="Mauve") {
          --ctp-accent: var(--ctp-mauve) !important;
      } else if (accent-color=="Red") {
          --ctp-accent: var(--ctp-red) !important;
      } else if (accent-color=="Maroon") {
          --ctp-accent: var(--ctp-maroon) !important;
      } else if (accent-color=="Peach") {
          --ctp-accent: var(--ctp-peach) !important;
      } else if (accent-color=="Yellow") {
          --ctp-accent: var(--ctp-yellow) !important;
      } else if (accent-color=="Green") {
          --ctp-accent: var(--ctp-green) !important;
      } else if (accent-color=="Teal") {
          --ctp-accent: var(--ctp-teal) !important;
      } else if (accent-color=="Blue") {
          --ctp-accent: var(--ctp-blue) !important;
      } else if (accent-color=="Sapphire") {
          --ctp-accent: var(--ctp-sapphire) !important;
      } else if (accent-color=="Sky") {
          --ctp-accent: var(--ctp-sky) !important;
      } else if (accent-color=="Lavender") {
          --ctp-accent: var(--ctp-lavender) !important;
      }  else if (accent-color=="Text") {
          --ctp-accent: var(--ctp-text) !important;
      }
    }
